/**
 * Navigation bar for PP app
 *
 * authored by Daniel Cheng dcheng47@gmail.com
 */
import { useState } from 'react';
import { Link } from 'react-router-dom';
import { ProSidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import { Button } from '@mui/material';
import {
  FaDev,
  FaUser,
  FaBasketballBall,
  FaDoorClosed } from "react-icons/fa";
import * as fcl from '@onflow/fcl';

fcl.config()
.put("accessNode.api", "https://access-testnet.onflow.org")
.put("discovery.wallet", "https://fcl-discovery.onflow.org/testnet/authn");

export default function PigskinAppBar(
  user,
  setUser
  ) {


  const state = {
    isSidebarCollapsed: true
  }

  const login = () => {
    fcl.authenticate();
    fcl.currentUser().subscribe(setUser);
  }

  const logout = () => {
    fcl.authenticate();
  }

  const toggleSidebar = () => {
    this.setState({isSidebarCollapsed: !state.isSidebarCollapsed});
  }

  return (
    <ProSidebar
      onClick={toggleSidebar}
      collapsed={state.isSidebarCollapsed}
    >
      <Menu iconShape="square">
        <MenuItem icon={<FaBasketballBall />}>
          <Link to="/">PP</Link>
        </MenuItem>
        <MenuItem icon={<FaUser />}>
          {user && user.addr ?
          <Link to="/account">
            {"Account Address: " + user.addr}
          </Link> :
          <Button color="inherit" onClick={login}>Login</Button>}
        </MenuItem>
        <MenuItem icon={<FaDev />}>
          <Link to="/dev">dev</Link>
        </MenuItem>
        {user &&
        <MenuItem icon={<FaDoorClosed />} onClick={logout}>
          Logout
        </MenuItem>}
      </Menu>
    </ProSidebar>
  );
}
