import logo from './logo.svg';
import './App.css';
import * as fcl from '@onflow/fcl';

fcl.config().put("accessNode.api", "https://access-testnet.onflow.org")

fcl.script`
  pub fun main() {
    return 2 + 2
  }
`
function send() {
  fcl.send([script]).then(async result => console.log(await fcl.decode(result)))
}


function App() {
  return (
    <div className="App">
    Hello
    <button onClick={send}>
    click me
    </button>
    </div>

  );
}

export default App;
