/**
 * Navigation bar for Pigskin app
 *
 * authored by Daniel Cheng dcheng47@gmail.com
 */
import * as React from 'react';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import { ProSidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import 'react-pro-sidebar/dist/css/styles.css';
import { Button } from '@mui/material';
import { MdOutlineSportsBasketball, FaDev } from "react-icons/fa";
import * as fcl from '@onflow/fcl';

fcl.config()
.put("accessNode.api", "https://access-testnet.onflow.org")
.put("discovery.wallet", "https://fcl-discovery.onflow.org/testnet/authn");

export default function PigskinAppBar() {
  const [user, setUser] = useState();

  const login = () => {
    fcl.authenticate();
    fcl.currentUser().subscribe(setUser);
  }

  const logout = () => {
    fcl.authenticate();
  }

  return (
    <ProSidebar>
      <Menu iconShape="square">
        <MenuItem icon={<MdOutlineSportsBasketball />}>
          <Link to="/">PP</Link>
        </MenuItem>
        <MenuItem>
          {user && user.addr
          ? "Account Address: " + user.addr
          : <Button color="inherit" onClick={login}>Login</Button>}
        </MenuItem>
        <MenuItem icon={<FaDev />}>
          <Link to="/dev">dev</Link>
        </MenuItem>
        <MenuItem onClick={logout}>
          Logout
        </MenuItem>
      </Menu>
    </ProSidebar>
  );
}
