/**
Pigskin's main page.
authored by Daniel Cheng dcheng47@gmail.com 2022
*/

import React from 'react';
import Button from 'react-bootstrap/Button'
import * as fcl from '@onflow/fcl';

fcl.config().put("accessNode.api", "https://access-testnet.onflow.org");

class Pigskin extends React.Component {

  constructor() {
    super()
    this.script = fcl.script`
      pub fun main(): Int {
        return 2 + 2
      }
    `

  }

  state = {
    answer: 0
  }


  send = (script) => {
     fcl.send([script])
      .then(
        async result => {
          this.setState({answer: await fcl.decode(result)});
      })

  }

  render() {
    return (
      <div>
        <div>ANSWER IS {this.state.answer}</div>
        < Button onClick={this.send(this.script)}>
          Send
        </Button>
      </div>
    )
  }
}

export default Pigskin;
