/**
Pigskin's main page.
authored by Daniel Cheng dcheng47@gmail.com 2022
*/

import {doTheThing} from '../src/cadence/scripts/doTheThing';
import * as fcl from '@onflow/fcl';
import {useState} from 'react';

fcl.config()
.put("accessNode.api", "https://access-testnet.onflow.org")
.put("discovery.wallet", "https://fcl-discovery.onflow.org/testnet/authn");

function Pigskin() {
  const [user, setUser] = useState();
  const [greeting, setGreeting] = useState();

  const login = () => {
    fcl.authenticate();
    fcl.currentUser().subscribe(setUser);
  }

  const getTheGreeting = async () => {

    const result = await fcl.send([
      fcl.script(doTheThing)
    ]).then(fcl.decode);

    setGreeting(result);
  }

  return (
    <div>
      <h1>Account Address: {user && user.addr ? user.addr : 'nothing'}</h1>
      <br/>
      {greeting ?? 'GoodBye Sun'}
      <br/>
      < button onClick={() => getTheGreeting()}>
        GREETINGS
      </button>
      <br/>
      < button onClick={() => login()}>
        LOGIN FIRST
      </button>
    </div>
  )
}

export default Pigskin;
