/**
 * Account page for PP app
 *
 * authored by Daniel Cheng dcheng47@gmail.com
 */
import {
  Box,
  CardHeader,
  Card,
} from '@mui/material';

export default function Account({
   user
  }) {

  if(!user) {
    return (<Redirect to='/'/>)
  } else {
    return (
      <Card sx={{
        display: 'flex',
        flexDirection: 'col'
      }}>
        <CardHeader>Account Page</CardHeader>
        <h1>Account Address: {user && user.addr ? user.addr : ""}</h1>
        <Box>
          ACCOUNT STUFF TBD
        </Box>
      </Card>

    );
  }
}
