import {invariant} from "@onflow/util-invariant"
import {query} from "../exec/query"

export const validateArgs = (msg, compSigs) => {
  invariant(/^[0-9a-f]+$/i.test(msg), "Signed message must be a hex string")
  invariant(
    Array.isArray(compSigs) &&
      compSigs.every((sig, i, arr) => sig.f_type === "CompositeSignature"),
    "Must include an Array of CompositeSignatures to verify"
  )
  invariant(
    compSigs.map(cs => cs.addr).every((addr, i, arr) => addr === arr[0]),
    "User signatures to be verified must be from a single account address"
  )
  return true
}

/**
 * Verify a valid signature/s for an account on Flow.
 *
 * @param {string} msg - A message string in hexadecimal format
 * @param {Array} compSigs - An array of Composite Signatures
 * @param {string} compSigs[].addr - The account address
 * @param {number} compSigs[].keyId - The account keyId
 * @param {string} compSigs[].signature - The signature to verify
 * @return {bool}
 *
 * @example
 *
 *  const isValid = await fcl.verifyUserSignatures(
 *    Buffer.from('FOO').toString("hex"),
 *    [{f_type: "CompositeSignature", f_vsn: "1.0.0", addr: "0x123", keyId: 0, signature: "abc123"}]
 *  )
 */
export async function verifyUserSignatures(msg, compSigs) {
  validateArgs(msg, compSigs)

  const acctAddress = compSigs[0].addr
  let signatures = []
  let keyIds = []
  compSigs.map(cs => {
    signatures.push(cs.signature)
    keyIds.push(cs.keyId)
  })

  return await query({
    cadence: VERIFY_SIG_SCRIPT,
    args: (arg, t) => [
      arg(acctAddress, t.Address),
      arg(msg, t.String),
      arg(keyIds, t.Array([t.Int])),
      arg(signatures, t.Array([t.String])),
    ],
  })
}

const VERIFY_SIG_SCRIPT = `
  import Crypto

  pub fun getHashAlgo(_ x: Int): HashAlgorithm {
    switch x {
    case 1:
        return HashAlgorithm.SHA2_256
    case 2:
        return HashAlgorithm.SHA2_384
    case 3:
        return HashAlgorithm.SHA3_256
    case 4:
        return HashAlgorithm.SHA3_384
    case 5:
        return HashAlgorithm.KMAC128_BLS_BLS12_381
    default:
        return HashAlgorithm.SHA3_256
    }
  }

  pub fun main(
    acctAddress: Address,
    message: String,
    keyIds: [Int],
    signatures: [String],
  ): Bool {

    let keyList = Crypto.KeyList()
    let account = getAccount(acctAddress)

    let rawPublicKeys: [String] = []
    let weights: [UFix64] = []
    let signAlgos: [UInt] = []
    let hashAlgos: [UInt] = []
    let uniqueKeys: {Int: Bool} = {}

    for id in keyIds {
      uniqueKeys[id] = true
    }

    assert(uniqueKeys.keys.length == keyIds.length, message: "Invalid KeyId: Duplicate key found for account")

    var counter = 0
    while (counter < keyIds.length) {
      let accountKey = account.keys.get(keyIndex: keyIds[counter]) ?? panic("Key provided does not exist on account")
      rawPublicKeys.append(String.encodeHex(accountKey.publicKey.publicKey))
      weights.append(accountKey.weight)
      signAlgos.append(UInt(accountKey.publicKey.signatureAlgorithm.rawValue))
      hashAlgos.append(UInt(accountKey.hashAlgorithm.rawValue))
      counter = counter + 1
    }

    var totalWeight = 0.0
    for weight in weights {
      totalWeight = totalWeight + weight
    }

    assert(totalWeight >= 1000.0, message: "Signature key weights do not meet threshold >= 1000.0")

    var i = 0
    for rawPublicKey in rawPublicKeys {
      keyList.add(
        PublicKey(
          publicKey: rawPublicKey.decodeHex(),
          signatureAlgorithm: signAlgos[i] == 2 ? SignatureAlgorithm.ECDSA_secp256k1  : SignatureAlgorithm.ECDSA_P256
        ),
        hashAlgorithm: getHashAlgo(Int(hashAlgos[i])),
        weight: weights[i]
      )
      i = i + 1
    }

    let signatureSet: [Crypto.KeyListSignature] = []

    var j = 0
    for signature in signatures {
      signatureSet.append(
        Crypto.KeyListSignature(
          keyIndex: j,
          signature: signature.decodeHex()
        )
      )
      j = j + 1
    }

    let signedData = message.decodeHex()

    return keyList.verify(
      signatureSet: signatureSet,
      signedData: signedData
    )
  }
`
import {COMPOSITE_SIGNATURE_PRAGMA} from "./__vsn"
import {sansPrefix} from "@onflow/util-address"

// {
//    "f_type": "CompositeSignature",
//    "f_vsn": "1.0.0",
//    "addr": "_____",         // sans-prefix
//    "signature": "adfe1234", // hex
//    "keyId": 3,
// }
export function normalizeCompositeSignature(resp) {
  if (resp == null) return null

  switch (resp["f_vsn"]) {
    case "1.0.0":
      return resp

    default:
      return {
        ...COMPOSITE_SIGNATURE_PRAGMA,
        addr: sansPrefix(resp.addr || resp.address),
        signature: resp.signature || resp.sig,
        keyId: resp.keyId,
      }
  }
}
import "../default-config"
import * as t from "@onflow/types"
import {account, arg, config} from "@onflow/sdk"
import {spawn, send, INIT, SUBSCRIBE, UNSUBSCRIBE} from "@onflow/util-actor"
import {withPrefix, sansPrefix} from "@onflow/util-address"
import {invariant} from "@onflow/util-invariant"
import {buildUser} from "./build-user"
import {serviceOfType} from "./service-of-type"
import {execService} from "./exec-service"
import {verifyUserSignatures as verify} from "../exec/verify"
import {normalizeCompositeSignature} from "./normalize/composite-signature"
import {getDiscoveryService} from "../config-utils"

const NAME = "CURRENT_USER"
const UPDATED = "CURRENT_USER/UPDATED"
const SNAPSHOT = "SNAPSHOT"
const SET_CURRENT_USER = "SET_CURRENT_USER"
const DEL_CURRENT_USER = "DEL_CURRENT_USER"

const DATA = `{
  "f_type": "User",
  "f_vsn": "1.0.0",
  "addr":null,
  "cid":null,
  "loggedIn":null,
  "expiresAt":null,
  "services":[]
}`

const getStoredUser = async storage => {
  const fallback = JSON.parse(DATA)
  const stored = await storage.get(NAME)
  if (stored != null && fallback["f_vsn"] !== stored["f_vsn"]) {
    storage.removeItem(NAME)
    return fallback
  }
  return stored || fallback
}

const HANDLERS = {
  [INIT]: async ctx => {
    if (typeof window === "undefined") {
      console.warn(
        `
        %cFCL Warning
        ============================
        "currentUser" is only available in the browser.
        For more info, please see the docs: https://docs.onflow.org/fcl/
        ============================
        `,
        "font-weight:bold;font-family:monospace;"
      )
    }

    ctx.merge(JSON.parse(DATA))
    const storage = await config.first(["fcl.storage", "fcl.storage.default"])
    if (storage.can) {
      const user = await getStoredUser(storage)
      if (notExpired(user)) ctx.merge(user)
    }
  },
  [SUBSCRIBE]: (ctx, letter) => {
    ctx.subscribe(letter.from)
    ctx.send(letter.from, UPDATED, {...ctx.all()})
  },
  [UNSUBSCRIBE]: (ctx, letter) => {
    ctx.unsubscribe(letter.from)
  },
  [SNAPSHOT]: async (ctx, letter) => {
    letter.reply({...ctx.all()})
  },
  [SET_CURRENT_USER]: async (ctx, letter, data) => {
    ctx.merge(data)
    const storage = await config.first(["fcl.storage", "fcl.storage.default"])
    if (storage.can) storage.put(NAME, ctx.all())
    ctx.broadcast(UPDATED, {...ctx.all()})
  },
  [DEL_CURRENT_USER]: async (ctx, letter) => {
    ctx.merge(JSON.parse(DATA))
    const storage = await config.first(["fcl.storage", "fcl.storage.default"])
    if (storage.can) storage.put(NAME, ctx.all())
    ctx.broadcast(UPDATED, {...ctx.all()})
  },
}

const spawnCurrentUser = () => spawn(HANDLERS, NAME)

function notExpired(user) {
  return (
    user.expiresAt == null ||
    user.expiresAt === 0 ||
    user.expiresAt > Date.now()
  )
}

async function makeMessage() {
  return {
    timestamp: Date.now(),
    appDomainTag: await config.get("fcl.appDomainTag"),
    extensions: window.fcl_extensions || [],
  }
}

async function authenticate({service, redir = false} = {}) {
  return new Promise(async (resolve, reject) => {
    spawnCurrentUser()
    const user = await snapshot()
    const msg = await makeMessage()
    const opts = {redir}
    const discoveryService = await getDiscoveryService()
    const refreshService = serviceOfType(user.services, "authn-refresh")
    const suppressRedirWarning = await config.get("fcl.warning.suppress.redir")

    if (redir && !suppressRedirWarning) {
      console.warn(
        `You are manually enabling a very experimental feature that is not yet standard, use at your own risk.
         You can disable this warning by setting fcl.warning.suppress.redir to true in your config`
      )
    }

    invariant(
      service || discoveryService.endpoint,
      `
        If no service passed to "authenticate," then "discovery.wallet" must be defined in config.
        See: "https://docs.onflow.org/fcl/reference/api/#setting-configuration-values"
      `
    )

    if (user.loggedIn) {
      if (refreshService) {
        try {
          const response = await execService({
            service: refreshService,
            msg,
            opts,
          })
          send(NAME, SET_CURRENT_USER, await buildUser(response))
        } catch (e) {
          console.error("Error: Could not refresh authentication.", e)
        } finally {
          return resolve(await snapshot())
        }
      } else {
        return resolve(user)
      }
    }

    try {
      const response = await execService({
        service: {
          ...(service || discoveryService),
          method: discoveryService?.method || service.method || "IFRAME/RPC"
        },
        msg,
        opts,
        config: {
          discoveryAuthnInclude: discoveryService.discoveryAuthnInclude
        }
      })
      send(NAME, SET_CURRENT_USER, await buildUser(response))
    } catch (e) {
      console.error("Error while authenticating", e)
    } finally {
      resolve(await snapshot())
    }
  })
}

function unauthenticate() {
  spawnCurrentUser()
  send(NAME, DEL_CURRENT_USER)
