/**
Pigskin's main page.
authored by Daniel Cheng dcheng47@gmail.com 2022
*/

import {doTheThing} from '../src/cadence/scripts/doTheThing';
import {doTheOtherThing} from '../src/cadence/transactions/doTheOtherThing';
import * as types from '@onflow/types';
import * as fcl from '@onflow/fcl';
import {useState} from 'react';

fcl.config()
.put("accessNode.api", "https://access-testnet.onflow.org")
.put("discovery.wallet", "https://fcl-discovery.onflow.org/testnet/authn");

function Pigskin() {
  const [user, setUser] = useState();
  const [greeting, setGreeting] = useState();

  const login = () => {
    fcl.authenticate();
    fcl.currentUser().subscribe(setUser);
  }

  const getTheGreeting = async () => {

    const result = await fcl.send([
      fcl.script(doTheThing)
    ]).then(fcl.decode);

    setGreeting(result);
  }

  const changeTheGreeting = async () => {
    const transactionId = await fcl.send([
      fcl.transaction(doTheOtherThing),
      fcl.args([
        fcl.arg("LATER", types.String)
      ]),
      fcl.payer(fcl.authz),
      fcl.proposer(fcl.authz),
      fcl.authorizations([fcl.authz]),
      fcl.limit(9999)
    ]).then(fcl.decode)

    console.log(transactionId);
  }

  return (
    <div>
      <h1>Account Address: {user && user.addr ? user.addr : 'nothing'}</h1>
      <br/>
      {greeting ?? 'GoodBye Sun'}
      <br/>
      < button onClick={() => getTheGreeting()}>
        GREETINGS
      </button>
      <br/>
      < button onClick={() => changeTheGreeting()}>
        GREETINGS CHANGE
      </button>
      <br/>
      < button onClick={() => login()}>
        LOG IN
      </button>
      < button onClick={fcl.unauthorize}>
        LOG OUT
      </button>
    </div>
  )
}

export default Pigskin;
